create table sample_restaurant_order_items (
    order_id     number
        generated by default on null as identity
    not null enable,
    item_id      number not null enable,
    unit_price   number,
    quantity     number,
    line_item_id number,
    utensils     varchar2(2 byte),
    note         varchar2(4000 byte)
);

alter table sample_restaurant_order_items
    add constraint sample_restaurant_order_items_pk primary key ( order_id,
                                                                  line_item_id )
        using index enable;


