create table sample_restaurant_orders (
    order_id       number
        generated by default on null as identity
    not null enable,
    order_datetime timestamp(6),
    order_status   varchar2(20 char),
    session_id     varchar2(100 char)
);

alter table sample_restaurant_orders
    add constraint sample_restaurant_orders_pk primary key ( order_id )
        using index enable;


